import React, { useState } from "react";
import * as Icon from "react-feather";
import { Formik } from "formik";
import * as Yup from "yup";
import { useNavigate, useLocation } from "react-router-dom";
import { invoiceProduct } from "../../../utils/validationSchema";
import { useDispatch, useSelector } from "react-redux";
import { DETAIL_INVOICE_REQUEST, EDIT_INVOICE_REQUEST } from "../../../redux/types/Invoice";
import { ClipLoader } from "react-spinners";
import { GSTValue, InvoiceTextType } from "../../../utils/staticDropdown";
import { Currency } from "../../../utils/currency";
import { decode } from "html-entities";
import { withDecimal } from "../../../utils/helper";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from "moment";

var converter = require("number-to-words");

const Modal = React.lazy(() => import("../../../components/common/Modal"));
const Input = React.lazy(() => import("../../../components/common/forms/Input"));
const InvoiceDetails = React.lazy(() => import("../../../components/common/InvoiceDetails"));
const SubmitInvoiceModal = React.lazy(() => import("../AddInvoice/SubmitInvoiceModal"));
const Select = React.lazy(() => import("../../../components/common/forms/Select"));
const CurrencySelect = React.lazy(() => import("react-select"));
const DropzoneComponent = React.lazy(() => import("../../../components/common/forms/Dropzone"));

const initialValuesObj = {
    name: "",
    description: "",
    qty: "",
    price: "",
    tax_type: "",
};

const EditInvoice = () => {
    const formRef = React.useRef("");
    const navigate = useNavigate();
    const location = useLocation();
    const dispatch = useDispatch();
    const printRef = React.useRef(null);
    const isEdit = location?.pathname?.includes("edit");

    const handleDownloadPdf = async () => {
        printRef.current.save();
    };

    const [initialValue, setInitialValue] = useState({
        billToCompanyName: "",
        billToGst: "",
        billToContact: "",
        billToAddress: "",
        billToEmail: "",
        billToLabel: "",
        billToName: "",
        gst: "",
        gstRate: "",
        invoice: "",
        invoiceNumber: "",
        productList: [],
        shipToAddress: "",
        shipToEmail: "",
        shipToLabel: "",
        shipToName: "",
        shipToCompanyName: "",
        shipToGst: "",
        shipToContact: "",
        subTotal: "",
        totalAmount: "",
        invoice_logo: "",
        currency: "",
        signature: "",
        duaDate: "",
    });
    const [initialValueModal, setInitialValueModal] = useState({});

    const validationSchema = Yup.object().shape({
        invoiceNumber: Yup.string().required("Please enter Invoice Number"),
        currency: Yup.string().required("Please select currency"),
        duaDate: Yup.string().required("Please select due date"),
        billToCompanyName: Yup.string().required("Please enter company name"),
        shipToCompanyName: Yup.string().required("Please enter company name"),
        billToAddress: Yup.string().required("Please enter address"),
        shipToAddress: Yup.string().required("Please enter address"),
        productList: Yup.array().required("Please add products").min(1),
    });

    const [isInvoiceDetailsVisible, setIsInvoiceDetailsVisible] = useState(false);

    const [showModal, setShowModal] = useState(false);
    const [editID, setEditID] = useState(false);
    const [initialValues, setInitialValues] = useState(initialValuesObj);
    const [isSubmitModalVisible, setIsSubmitModalVisible] = useState(false);
    const [apiValue, setAPIValue] = useState({});
    const [isLoading, setIsLoading] = useState(false);
    const [apiLoading, setAPILoading] = useState(false);

    const [invoiceNameWidth, setInvoiceNameWidth] = useState(12);

    const [invoiceNumberWidth, setInvoiceNumberWidth] = useState(17);

    const state = useSelector((state) => state);
    const { mode } = useSelector((state) => state.persist);

    const colourStyles = {
        menuPortal: (base) => ({ ...base, zIndex: 9999 }),
        control: (styles) => ({
            ...styles,
            backgroundColor: mode === "dark" ? "#1b253b" : "white",
            borderColor: "#e2e8f0",
            color: mode === "dark" ? "#FFFFFF" : "#384252",
            paddingRight: "4px",
            paddingLeft: "4px",
            minHeight: 38,
        }),
        option: (styles, { data, isDisabled, isFocused, isSelected }) => {
            return {
                ...styles,
                //backgroundColor: "white",
                cursor: isDisabled ? "not-allowed" : "default",
                border: isFocused ? "0px" : "0px",
                ":active": {
                    ...styles[":active"],
                },
                ":hover": {
                    ...styles[":hover"],
                    backgroundColor: mode === "dark" ? "#0f1d36" : "#b1d7ff",
                },
            };
        },
        input: (styles, { isFocused }) => ({
            ...styles,
            ":active": {
                border: "none",
            },
            border: 0,
            // This line disable the blue border
        }),
        placeholder: (styles, { isFocused }) => ({
            ...styles,
            boxShadow: "none",
            // This line disable the blue border
        }),
        singleValue: (styles, { data }) => ({
            ...styles,
            color: mode === "dark" ? "#FFFFFF" : "#384252",
        }),
        menu: (styles, { data }) => ({ ...styles, backgroundColor: mode === "dark" ? "#1b253b" : "white" }),
    };

    React.useEffect(() => {
        if (Object.keys(state?.invoice?.invoiceDetail).length) {
            const initialData = {
                billToAddress: state?.invoice?.invoiceDetail?.billToAddress,
                billToCompanyName: state?.invoice?.invoiceDetail?.billToCompanyName,
                billToGst: state?.invoice?.invoiceDetail?.billToGst,
                billToContact: state?.invoice?.invoiceDetail?.billToContact,
                billToEmail: state?.invoice?.invoiceDetail?.billToEmail,
                billToLabel: state?.invoice?.invoiceDetail?.billToLabel,
                billToName: state?.invoice?.invoiceDetail?.billToName,
                gst: state?.invoice?.invoiceDetail?.gst,
                gstRate: state?.invoice?.invoiceDetail?.gstRate,
                invoice: state?.invoice?.invoiceDetail?.invoice,
                invoiceNumber: state?.invoice?.invoiceDetail?.invoiceNumber,
                productList: JSON.parse(state?.invoice?.invoiceDetail?.productList),
                shipToAddress: state?.invoice?.invoiceDetail?.shipToAddress,
                shipToEmail: state?.invoice?.invoiceDetail?.shipToEmail,
                shipToLabel: state?.invoice?.invoiceDetail?.shipToLabel,
                shipToName: state?.invoice?.invoiceDetail?.shipToName,
                shipToCompanyName: state?.invoice?.invoiceDetail?.shipToCompanyName,
                shipToContact: state?.invoice?.invoiceDetail?.shipToContact,
                shipToGst: state?.invoice?.invoiceDetail?.shipToGst,
                subTotal: state?.invoice?.invoiceDetail?.subTotal,
                totalAmount: state?.invoice?.invoiceDetail?.totalAmount,
                invoice_logo: state?.invoice?.invoiceDetail?.invoice_logo,
                currency: state?.invoice?.invoiceDetail?.currency,
                signature: state?.invoice?.invoiceDetail?.signature,
                duaDate: new Date(state?.invoice?.invoiceDetail?.duaDate),
            };

            setInitialValue(initialData);
            setInitialValueModal({
                name: state?.invoice?.invoiceDetail?.name,
                isPriceEditable: state?.invoice?.invoiceDetail?.isPriceEditable === 1 ? true : false,
            });
        }
    }, [state?.invoice?.invoiceDetail]);

    const onCloseModal = () => {
        setShowModal(false);
        setEditID("");
    };

    // const handleChangeGST = (value) => {
    //     formRef.current?.setFieldValue("gst", value);
    //     handleChangeProductList(undefined, value);
    // };

    const handleChangeProductList = (productList, gst) => {
        const productListClone = productList || formRef.current?.values?.productList;
        const gstClone = gst ?? formRef.current.values?.gst;
        const subTotal = productListClone.reduce((previousValue, currentValue) => {
            return previousValue + currentValue.price * currentValue.qty;
        }, 0);

        formRef.current?.setFieldValue("subTotal", subTotal.toFixed(2));
        const gstRate = subTotal * (gstClone / 100);
        formRef.current?.setFieldValue("gstRate", gstRate.toFixed(2));
        let totalFormAmount = 0;
        {
            productList.map((item, index) => {
                const amount = item?.qty * item?.price;
                const igstP = item?.tax_value;
                const totalAmount = amount + (amount * igstP) / 100;
                totalFormAmount = totalFormAmount + totalAmount;
            });
        }
        formRef.current?.setFieldValue("totalAmount", totalFormAmount.toFixed(2));
    };

    const onClickAddNewProduct = () => {
        setInitialValues(initialValuesObj);
        setShowModal(true);
    };

    const onClickSaveProduct = (value) => {
        const products = [...formRef.current?.values?.productList];
        let genrateId = products[products?.length - 1]?.id;
        genrateId = genrateId ? genrateId + 1 : 1;
        products.push({ ...value, id: genrateId });
        handleChangeProductList(products);
        formRef.current?.setFieldValue("productList", products);
        onCloseModal();
    };

    const onClickEditProduct = (item) => {
        setEditID(item?.id);
        setInitialValues({
            name: item?.name,
            description: item?.description,
            qty: item?.qty,
            price: item?.price,
            tax_type: item?.tax_type,
            tax_value: item?.tax_value,
        });
        setShowModal(true);
    };

    const onSubmitkEditProduct = (value) => {
        const products = [...formRef.current?.values?.productList];
        const craeteNewData = [];
        products?.forEach((item) => {
            if (editID === item?.id) {
                craeteNewData.push({
                    ...value,
                    id: editID,
                    qty: value?.qty < 0 ? value?.qty * -1 : value?.qty,
                    price: value?.price < 0 ? value?.price * -1 : value?.price,
                });
            } else {
                craeteNewData.push({
                    ...value,
                    qty: value?.qty < 0 ? value?.qty * -1 : value?.qty,
                    price: value?.price < 0 ? value?.price * -1 : value?.price,
                });
            }
        });
        handleChangeProductList(craeteNewData);
        formRef.current?.setFieldValue("productList", craeteNewData);
        onCloseModal();
        setEditID("");
    };

    const onClickDeleteProduct = (id) => {
        const filterProduct = formRef.current?.values?.productList.filter((item) => item?.id !== id);
        handleChangeProductList(filterProduct);
        formRef.current?.setFieldValue("productList", filterProduct);
    };

    const onSubmit = (values) => {
        setIsSubmitModalVisible(true);
        setAPIValue({
            ...values,
            duaDate: values?.duaDate ? moment(values?.duaDate).format("YYYY-MM-DD") : undefined,
            productList: JSON.stringify(values?.productList),
        });
    };

    React.useEffect(() => {
        if (isEdit) {
            const callBack = () => {
                setIsLoading(false);
            };

            setIsLoading(true);
            const array = location?.pathname?.split("/");
            const id = array[array?.length - 1];

            const navigateListing = () => {
                navigate("/invoice");
            };

            dispatch({ type: DETAIL_INVOICE_REQUEST, payload: { invoice_id: id }, callBack, navigateListing });
        }
    }, []);

    const onSubmitAPI = (value) => {
        const array = location?.pathname?.split("/");
        const id = array[array?.length - 1];
        const payload = {
            ...apiValue,
            ...value,
            invoice_id: id,
        };

        const formData = new FormData();

        for (const property in payload) {
            if (property === "invoice_logo") {
                if (payload[property]?.includes("https://") || payload[property]?.includes("http://") || !payload["invoice_logo"]) {
                } else {
                    formData.append(property, payload[property][0]);
                }
            } else if (property === "isPriceEditable") {
                formData.append(property, payload[property] ? 1 : 0);
            } else {
                formData.append(property, payload[property]);
            }
        }

        // values
        const callBack = () => {
            setAPILoading(false);
        };

        const navigateState = () => {
            setIsSubmitModalVisible(false);
            navigate(`/invoice`);
        };
        setAPILoading(true);
        dispatch({ type: EDIT_INVOICE_REQUEST, payload: formData, callBack, navigateState });
    };

    const _renderAmountSection = (values) => {
        const amount = values?.qty * values?.price;
        const igstP = values?.tax_value;
        const sgstCgstP = values?.tax_value / 2;
        const igst = (amount * igstP) / 100;
        const sgstCgst = (amount * sgstCgstP) / 100;
        const totalAmount = amount + (amount * igstP) / 100;
        return (
            <div className="flex flex-col items-end">
                {values?.tax_type != "" && values?.tax_type != "1" && values?.tax_value != "" && (
                    <div className="text-base text-slate-500 font-bold mt-3">
                        Amount: <span>{decode(Currency.find((c) => c?.value === values?.currency)?.symbol)}</span>
                        {amount.toFixed(2)}
                    </div>
                )}
                {values?.tax_type == "2" && values?.tax_value != "" && (
                    <div className="text-base text-slate-500 font-bold">
                        IGST: <span>{decode(Currency.find((c) => c?.value === values?.currency)?.symbol)}</span>
                        {igst.toFixed(2)} ({igstP}%)
                    </div>
                )}
                {values?.tax_type == "3" && values?.tax_value != "" && (
                    <div className="text-base text-slate-500 font-bold">
                        SGST: <span>{decode(Currency.find((c) => c?.value === values?.currency)?.symbol)}</span>
                        {sgstCgst.toFixed(2)} ({sgstCgstP}%)
                    </div>
                )}
                {values?.tax_type == "3" && values?.tax_value != "" && (
                    <div className="text-base text-slate-500 font-bold">
                        CGST: <span>{decode(Currency.find((c) => c?.value === values?.currency)?.symbol)}</span>
                        {sgstCgst.toFixed(2)} ({sgstCgstP}%)
                    </div>
                )}
                {values?.price != "" && (
                    <div className="text-xl text-blue-800 dark:text-white font-bold mt-2">
                        Total Amount: <span>{decode(Currency.find((c) => c?.value === values?.currency)?.symbol)}</span>
                        {totalAmount.toFixed(2)}
                    </div>
                )}
            </div>
        );
    };

    return (
        <>
            <SubmitInvoiceModal
                isLoading={apiLoading}
                editInitialValues={initialValueModal}
                onClickSave={onSubmitAPI}
                data={formRef?.current?.values}
                showModal={isSubmitModalVisible}
                onCloseModal={() => setIsSubmitModalVisible(false)}
            />
            <Modal
                heading={"Invoice"}
                visible={showModal}
                onClose={onCloseModal}
                onClickSave={editID ? onSubmitkEditProduct : onClickSaveProduct}
                onClickCancel={onCloseModal}
                modalMinWidth={"50%"}
                func
                useFormik
                initialValues={initialValues}
                validationState={invoiceProduct}>
                {(setFieldValue, values, errors) => {
                    return (
                        <>
                            <Input
                                type="text"
                                className="intro-x login__input form-control py-2 px-3 block"
                                placeholder={"Product Name"}
                                name={"name"}
                                label="Product Name"
                                isRequiredField
                            />
                            <Input
                                labelRightContent={
                                    <div
                                        className={`border-2 h-[30px] w-[30px] ${
                                            values?.description?.length >= 100
                                                ? "border-red-600 text-red-600"
                                                : values?.description?.length >= 80
                                                ? "border-yellow-600 text-yellow-600"
                                                : "border-green-600 text-green-600"
                                        } rounded-full flex justify-center items-center`}>
                                        {100 - values?.description?.length}
                                    </div>
                                }
                                textarea="true"
                                type="text"
                                maxlength={100}
                                className="intro-x login__input form-control py-2 px-3 block"
                                placeholder={"Product Description"}
                                name={"description"}
                                label="Product Description"
                                containerClassName="mt-3"
                            />
                            {/* <p className="">{100 - values?.description?.length}</p> */}
                            <div className="grid grid-cols-12 gap-4 gap-y-5 mt-5">
                                <div
                                    className={
                                        values?.tax_type != "" && values?.tax_type != "1"
